(function(tinymce) {
	tinymce.create('tinymce.plugins.dif_imagesPlugin', {
		init : function(ed, url) {
			// Register commands
			ed.addCommand('dif_images', function() {
				ed.windowManager.open({
					file : url + '/dif_images.php',
					width : 1024 + parseInt(ed.getLang('dif_images.delta_width', 0)),
					height : 768 + parseInt(ed.getLang('dif_images.delta_height', 0)),
					inline : 1
				}, {
					plugin_url : url
				});
			});

			// Register buttons
			ed.addButton('dif_images', {title : 'dif_images.dif_images_desc', cmd : 'dif_images', image : url + '/img/bouton.png'});
		},

		getInfo : function() {
			return {
				longname : 'Lolteams',
				author : 'RECASENS Xavier',
				authorurl : 'http://xrecasens.info',
				infourl : 'http://xrecasens.info',
				version : '0.1'
			};
		}
	});

	// Register plugin
	tinymce.PluginManager.add('dif_images', tinymce.plugins.dif_imagesPlugin);
})(tinymce);